mapper.update:
UPDATE album_like
SET score = #{score}
WHERE album_id = #{albumId} AND user_id = #{userId};

mapper.delete:
DELETE
FROM album_like
WHERE album_id = #{albumId} AND user_id = #{userId};

mapper.select:
SELECT id, album_id, user_id, score
FROM album_like
WHERE album_id = #{albumId} AND user_id = #{userId};
